mapper.select:
select t1.user_id, t1.user_name, t1.password, t1.profile_picture, t1.name, t1.sex, t1.birthday, t1.address, t1.email, t1.department, t1.introduction, t1.enabled, t1.create_time, t1.modify_time, IF(count(distinct t2.record_id) > 0, 1, 0) as locked from t_user t1 left join t_user_locked_record t2 on t1.user_id = t2.user_id and t2.unlocked_time > now()
t1.user_name = #{userName}
group by t1.user_id, t1.user_name, t1.password, t1.profile_picture, t1.name, t1.sex, t1.birthday, t1.address, t1.email, t1.department, t1.introduction, t1.enabled, t1.create_time, t1.modify_time

mapper.update:

UPDATE t_user
SET enabled = #{enabled}
WHERE
	user_id = #{userId}
